<?php
$testresults = 'test_results.xml';
$results_file_path = 'results.xml';
$event_file_path = 'ElectionEvent.xml';

$city_server_event = 'http://www.london.ca/elections/results/ElectionEvent.xml';
$city_server_result = "http://www.london.ca/elections/results/results.xml";

// Create the stream context
$context = stream_context_create(array(
    'http' => array(
        'timeout' => 10     // Timeout in seconds
    )
));
//ElectionEvent.xml should not change

//TODO: WHAT SERVER IS THIS ON ... DO WE KEEP 1 VERSION OF ElectionEvent.xml OR LOAD IT EVERY TIME ?

//$event_file = file_get_contents($event_file_path);
//$results_file = file_get_contents($results_file_path);
$results_file = file_get_contents($city_server_result);

$event_file = file_get_contents($city_server_event);
//$results_file = file_get_contents(test_results.xml);

// Check for empties
if (!empty($results_file))
{
	//if file is good, save it locally 
	file_put_contents($results_file_path, $results_file);
}
else{
	//if file is not available from city servers, load last saved file
    $results_file = file_get_contents($results_file_path);
}

$xmlresults = new SimpleXMLElement($results_file);
$xmlevent = new SimpleXMLElement($event_file);

///ADD A NEW CHILD OF THE XMLELEMENT OBJECT TO HOLD THE RESULTS.XML ATTRIBUTES
$xmlevent->addChild('results', '');
foreach ($xmlresults->attributes() as $a => $b) {	
	$xmlevent->results->addAttribute($a, $b);	
}

//TURN ALL THE RESULTS.XML ITEMS INTO ARRAYS (CHOICES, CONTESTS, AREAS) WITH THE ID AS THE ARRAY KEY SO THEY
// CAN EASILY BE REFERENCED LATER
$choices = array();
$contests = array();
$areas = array();

foreach ($xmlresults->choices->choice as $item) {	
	addtoArray($item, $choices);
}

foreach ($xmlresults->contests->contest as $item) {	
	addtoArray($item, $contests);
}

foreach ($xmlresults->areas->area as $item) {
	addtoArray($item, $areas);
}

function addtoArray($item, &$arr){
		
	$item_atts = $item->attributes();
	foreach($item->attributes() as $a => $b) {	
		$arr[intval($item_atts['id'])][$a] = intval($b);
	}	
}

//NOW WE NEED TO MATCH THOSE RESULT ARRAYS TO THE APPROPRIATE CHOICE, CONTEST OR AREA

foreach($xmlevent->choices->choice as $item){				
	addAttribut($item, $choices );
}

foreach($xmlevent->contests->contest as $item){				
	addAttribut($item, $contests );
}

foreach($xmlevent->areas->area as $item){				
	addAttribut($item, $areas );
}

function addAttribut($item, $arr )
{
	$item_atts = $item->attributes();	
	while (list($key, $value) = each($arr[ intval($item_atts['id'])] ) ) {
		if($key != id)
		{
			$item->addAttribute( $key, $value);
		}
	}	
}

print(str_replace('@attributes', 'attributes', json_encode($xmlevent)));
